i=0
#==> création du tableau nbcarac qui contient le nombre de caractères des pseudos
nbcarac=[]
while i<tab.size 
    nbcarac<< tab[i].size
    i+=1
end
#==> force le lancement de ma boucle while principale jusqu'à ce que l'utilisateur décide de ne plus vouloir interragir avec le tableau
answer="O"

#Menu/boucle while principale : demande à l'utilisateur quelle action il souhaite effectuer
while answer=="o" || answer=="O"
    puts "Bonjour, à quelle question souhaitez vous répondre à propos de mon tableau ?"
    puts "1: Combien de handles se trouvent dans mon tableau ?"
    puts "2: Quel est le handle le plus court de mon tableau ?"
    puts "3: Combien mon tableau possède-t-il de handles avec 5 caractères ?"
    puts "4: Combien de handles commencent par une majuscule ?"
    puts "5: Trier les handles par ordre alphabétique"
    puts "6: Trier par ordre croissant de tailles"
    puts "7: Quelle est la position du handle @epenser ?"
    puts "8: Répartition des handles par tailles"
    print ">"
    choice=gets.chomp.to_i
    
    if choice==1
        #Combien de handles dans ce tableau ?
        puts "Il y a #{tab.size} handles dans ce tableau !"
    
    elsif choice==2
        #Quel est le handle le plus court de la liste ?
        smallIndex=nbcarac.find_index(nbcarac.min)
        puts "Le handle le plus court de la liste est: #{tab[smallIndex]}."
    
    elsif choice==3
        #Combien de handles avec 5 caractères ?
        i=0
        nbcarac=[]
        fivecarac=[]
        while i<tab.size
            nbcarac<< tab[i].size
            if nbcarac[i]==6
                fivecarac<<tab[i]
            end
            i+=1
        end
        puts "Il y a #{fivecarac.size} handles de 5 caractères dans ce tableau !"
    
    elsif choice==4
        #Combien commencent par une majuscule ?
        i=0
        maj=[]
        while i<tab.size
            firstcarac = tab[i][1]
            if /[[:upper:]]/.match(firstcarac)
                maj<<tab[i]
            end
            i+=1
        end
        puts "Il y a #{maj.size} handles qui commencent par une majuscule."
    
    elsif choice==5
        #Trier la liste de handles par ordre alphabétique :
        puts "Voici le tableau trié par ordre alphabétique :"
        puts""
        puts tab.sort_by(&:downcase)
    
    elsif choice==6
        #Trier par taille croissante :
        i=0
        x=4 #==>nbcarac.min=4
        order=[]
        puts "Tableau trié par taille de handles :"
        puts ""
        while x<34  #===>nbcarac.max=33
            while i<nbcarac.size
            if nbcarac[i]==x
                order<<tab[i]
            end
            i+=1
        end
            puts order
        order=[]
        i=0
        x+=1
        end
    
    elsif choice==7
        #Quelle est la position du handle @epenser ?
        puts "Le handle @epenser se trouve à l'index #{tab.find_index("@epenser")} tableau, c'est à dire à la position #{tab.find_index("@epenser") +1}."
    
    elsif choice==8
         #Répartition des handles par tailles :
        i=0
        x=3
        nborder=[]
        #===> nbcarac.max=33
        while x<33
            puts "Nombre de handles de #{x} caractères :"
            while i<381
                if nbcarac[i]-1==x
                    nborder<<tab[i]
                end
            i+=1
            end
            puts nborder.size
            nborder=[]
            i=0
            x+=1
        end
    else 
        puts "Ce n'est pas une valeur correcte... Monstre !"
        exit
    end
    puts "Voulez-vous interragir à nouveau avec mon tableau ? (O/N)"
    print ">"
    answer=gets.chomp
end

if answer=="n" || answer=="N"
    puts "Au revoir !"
    exit
elsif answer!="n" && answer!="N" && answer!="o" && answer!="O"
    puts "Pas compris... (O/N)"
    answer==gets.chomp
end


        